80分代码：  
```cpp
#include<bits/stdc++.h>
using namespace std;
int main()
{
	int n,ans=0,pls,pt;
	bool start;
	scanf("%d",&n);
	int block[n+5],goal[n+5];
	for(int i=1;i<=n;i++)
		scanf("%d",&goal[i]);
	memset(block,0,sizeof(block));
	while(1)
	{
		start=false;
		pls=100005;
		pt=-1;
		for(int i=1;i<=n;i++)
		{
			if(!start&&goal[i]!=block[i])
				start=true;
			if(start)
			{
				if(block[i]==goal[i])
					break;
				pls=min(pls,goal[i]-block[i]);
				pt=max(pls,pt);
				block[i]+=pls;
			}
		}
		if(!start)
		{
			printf("%d\n",ans);
			return 0;
		}
		ans+=pt;
	}
}
