{
	if(l==r)//如果触底就返回
	{
		ans[s]=l;
		return;
	}
	ll mid=(l+r)>>1;
	build(l,mid,ls(s));//二分建树
	build(mid+1,r,rs(s));
	ll s1,s2;
	s1=a[ans[ls(s)]];//列出左右子树中最低处的高
	s2=a[ans[rs(s)]];
	if(s1<s2)ans[s]=ans[ls(s)];//返回最低处
