void f(ll l,ll r,ll h)//h是之前的高度
{
	if(l>r)return;//奇怪的判断，分别判断这个区间是否存在，是否越界*2
	if(r<1)return;
	if(l>n)return;
	ll lo=find(1,1,n,l,r),height;
	height=a[lo];
	answer+=height-h;//answer加上（当前高度-之前的高度）
	f(l,lo-1,height);//二分
	f(lo+1,r,height);
}
int main()
{
	n=read();
	for(ll i=1;i<=n;i++)a[i]=read();
	build(1,n,1);
	f(1,n,0);
	cout<<answer;
} 
