#include<iostream>
#include<cstring>
#include<cstdio>
using namespace std;
const int maxN=100100;
int n,h[maxN];

inline int dg(int l,int r,int high)
{
    //cout<<l<<" "<<r<<" "<<high<<endl;
    if(l>r)
        return 0;
    if(l==r)
        return h[l]-high;
    int p1=h[l],p2=l;
    for(int i=l+1;i<=r;++i){
        if(p1>h[i]){
            p1=h[i];
            p2=i;
        }
    }
    int nk=p1-high+dg(l,p2-1,p1)+dg(p2+1,r,p1);
    return nk;
}

int main()
{
    cin>>n;
    for(int i=1;i<=n;++i)
        cin>>h[i];
    int ans=dg(1,n,0);
    cout<<ans<<endl;
    return 0;
}
