```cpp
#include<cstdio>
#include<cstring>
#include<iostream>
using namespace std;
int n,ans=0;
int a[100001]; 
int doit(int l,int r)
{
    int i,j,mn=1000000;
    if (l>r) return 0;  //不要忘了返回条件 
    for (i=l;i<=r;i++)  //在整个区间内查找最小高度 
        if (a[i]<mn)
        {
            mn=min(mn,a[i]);
            j=i;
        }
    for (i=l;i<=r;i++)  //把此区间内的所有位置减去最小高度 
      a[i]-=mn;
    ans+=mn; //为了达到这个高度，就一定要经过mn次 
    doit(l,j-1);  // 此时最小高度的位置的高度变成了0，这就把整个区间分成了两部分 
    doit(j+1,r);  //再把这两部分做上述处理 
}
int main()
{
    scanf("%d",&n);
    for (int i=1;i<=n;i++)
      scanf("%d",&a[i]);
    doit(1,n);
    printf("%d",ans);
    return 0;
}
```

