```cpp

# include <algorithm>
# include <iostream>
# include <cstring>
# include <cstdio>
# include <queue>
# include <cmath>
# define R register
# define LL long long
# define mod 100000007

using namespace std;

int n,pri[2010],p,num[2010],ans=1;
bool v[6010];

inline void in(R int &a){
    R char c = getchar();R int x=0,f=1;
    while(!isdigit(c)) {if(c == '-') f=-1; c=getchar();}
    while(isdigit(c)) x=(x<<1)+(x<<3)+c-'0',c = getchar();
    a=x*f;
}

inline int powe(R int x,R int r){
    R int ret=1;
    while(r){
        if(r&1) ret = ret*x%mod;
        if(r>1) x = x*x%mod;
        r>>=1;
    }
    return ret;
}

inline int youngsc(){
    in(n);
    for(R int i=2; i<=(n<<1); ++i)
    {
        if(!v[i]) pri[++p] = i;
        for(R int j=1; j<=p&&pri[j]*i<=(n<<1); ++j)
        {
            v[pri[j]*i] = 1;
            if(i%pri[j] == 0) break;
        }
    }
    for(R int i=1; i<=p; ++i)
    {
        R int d = n<<1;
        while(d)
        {
            num[i]+=d/pri[i];
            d/=pri[i];
        }
        d = n;
        while(d)
        {
            num[i]-=((d/pri[i])<<1);
            d/=pri[i];
        }
    }
    R int d=n+1;

    for(R int j=1; j<=p&&d>1; ++j)
        while(d%pri[j] == 0) d/=pri[j],num[j]--;

 for(R int i=1; i<=p; ++i)
        ans = 1LL*ans*powe(pri[i],num[i])%mod;
