这道题其实不难，用栈就可以了
```cpp
#include<bits/stdc++.h>
using namespace std;
int main(){
	/*| § ^_^ §|*/
	stack<long long> a;
	stack<char> b;
	string s,us;
	long long head,tail,n=0;//初始化
	head=0;//初始化
	cin>>s;
	long long o=0;//初始化
	long long l=s.size();//s的长度
	for(long long i=0;i<l;i++){
		if(s[i]=='+'||s[i]=='*'||i==l-1){
			tail=i;
			long long u1=0;
			if(i==l-1){//最极端情况，把string变int
				for(long long j=head;j<=tail;j++){
					u1=u1*10;
					u1=u1+(s[j]-'0');
				}
			}
			else{//一般情况，把string变int
				for(long long j=head;j<tail;j++){//要先算乘法，加法后面一起
					u1=u1*10;
					u1=u1+(s[j]-'0');
				}
			}
			if(!b.empty()&&!a.empty()&&b.top()=='*'){//判断a和b是不是空了
				long long u2=a.top();
				a.pop();
				u1=(u1*u2)%10000;//因只要取4位，所以只要算后4位
				b.pop();
			}
			head=i+1;
			a.push(u1);//把算出来的数再放回a
			b.push(s[i]);//把s[i]放进b
			n++;
		}
	}
	long long ks=0;
	while(!a.empty()&&!b.empty()){//一直加到a或b空了为止
		ks=(ks+a.top())%10000;//加一次取一次模，可以保证ks不会太大
		a.pop();//消除加过的数
		b.pop();//消除用过的符号
	}
	cout<<ks;//输出答案
	return 0;
}
