```cpp
#include<iostream>
#include<cstdio>
#include<cstring>
#include<algorithm>
#include<cmath>
using namespace std;
int sz[2000001],fh[1000001],l,si=0,fi=0,flag=1;	
//sz数字栈  fh符号栈  l长度  si数字栈指针 fi符号栈指针 flag判断数字是否读尽
char s[2000001];
//表达式数组
int ac(char c){				//判断运算符号的优先级
	if(c=='+') return 1;	//1为加
	if(c=='*') return 2;	//2为乘
	return -999;			//各类符号的优先级
}
int main(){
	fh[0]=-1;				//初始栈底优先级最小
	scanf("%s",s);
	int l=strlen(s);
	for(int i=0;i<l;i++){
		if(flag==1){		//如果上一个数已经读尽
			si++; flag=0;	//指针往后一个放新的数，标记数还没读尽
		}
		if(s[i]>='0'&&s[i]<='9'&&flag==0){ //如果是数字（其实可以和上面合并）
			sz[si]=sz[si]*10+s[i]-'0'; //一位一位往里存
		}
		else{				//如果是字符
			flag=1;			//标记数以读尽
			int t=ac(s[i]);
			if(t>fh[fi]) fh[++fi]=t; //优先级高就入栈
			else{
				while(t<=fh[fi]){ //优先级低于当前栈顶符号时
					if(fh[fi]==1){ //栈顶是加号
						si--;
						sz[si]=(sz[si]+sz[si+1])%10000; 
                		//将数字栈顶两个元素取出做加法运算
						sz[si+1]=0; //别忘把栈顶元素弹出
					}
					if(fh[fi]==2){ //栈顶是乘号
						si--;
						sz[si]=(sz[si]*sz[si+1])%10000; 	
                        //将数字栈顶两个元素取出做乘法运算
						sz[si+1]=0; //栈顶弹出
					}
					fh[fi]=0; 
					fi--; //符号栈顶弹出
				}
				fh[++fi]=t; //新符号入栈
			}
		}
	}
	while(fi!=0){
		if(fh[fi]==1) sz[si-1]=(sz[si-1]+sz[si])%10000;
		if(fh[fi]==2) sz[si-1]=(sz[si-1]*sz[si])%10000;
		fi--; si--;
        //处理两个栈中剩下的元素
	}
	cout<<sz[1]%10000; //时刻记住%10000
	return 0;
} 
```

