            continue;
        if(line[i] == '+')
        {
            ans = (ans + s.top()) % MOD;
            s.pop();
        }
        else if(line[i] == '*')
        {
            x = s.top()%MOD;
            s.pop();
            y = s.top()%MOD;
            s.pop();
