#include<queue>
#include<cstdio>
const int N=1001,M=10001;
using namespace std;
int n,m,k,in[N],head[N],num;
struct edge{int to,next;}e[M];
void add(int a,int b)  //建边 
{
    e[++num].next=head[a];
    head[a]=num;
    e[num].to=b;
    in[b]++;   //把加入度放这里 
}
queue<int>q;
bool topo() //简单的拓扑 
{
    int cnt=0;
    for (int i=1;i<=n;i++)
        if (!in[i])q.push(i);
    while (q.size())
    {
        int p=q.front();q.pop();cnt++;
        for (int i=head[p];i;i=e[i].next)
        {
            int v=e[i].to;
            if (!--in[v])q.push(v);
        }
    }
    if (cnt<n)return 0;  //如果处理的点数不足n则说明有环 
    return 1;
}
int main()
{
    scanf("%d%d%d",&n,&m,&k);
    while (m--)
    {
        int u,v;
        scanf("%d%d",&u,&v);add(u,v);
