        puts("Yes");
        int base=2,t=1; //坑点，这道题k的意义就在于此 
        while (k)
        {
            if (k&1==1)t=t*base%9997;
            base=base*base%9997;
              k/=2;
        }  //仿照kkk(dalao)的写法(请勿吐槽) 
        printf("%d",t);
    }
    else puts("No"),printf("%d",k*k);
    return 0;
}
