	char c;  int x=0,op=0,flag=0;
	a[1]=a[2]=a[3]=0;
	// 处理出x，y，z 
	while(cin>>c)
	{
	  if(c=='C')a[op]+=x,op=1,x=1,flag=1;
	  else if(c=='H')a[op]+=x,op=2,x=1,flag=1;
	  else if(c=='O')a[op]+=x,op=3,x=1,flag=1;
	  else if(isdigit(c)){
	  	if(flag)x=0;
	    x=(x<<3)+(x<<1)+(c^48);
	    flag=0;
	  }
	}
	a[op]+=x;
