	while(a[2]%2!=0||(a[2]-a[3]*2)%4!=0){
	  a[1]<<=1;  a[2]<<=1;  a[3]<<=1;
	}
	cout<<a[1]<<" "<<(a[2]>>1)<<endl;
	return 0;
}
