	for (int i = 0; i < n; i++) next[i] = i + 1;
	next[n] = 1;
	int position = 0;
	for (int i = 1; i <= n; i++) {
		for (int j = 1; j < m; j++)
			position = next[position];
		printf("%d ", next[position]);
		next[position] = next[next[position]];
	}
	return 0;
