const int Maxn=3e5+7;
#define lowbit(x) ((x)&(-x))
int N,m,pos,a[Maxn];
int p;
int search(int x) {
//    printf("\ndasasd %d\n",x);
    int bz = 20;
    int g,c=0,i;
    for(;bz!=-1;--bz) {
        g = 1<< bz;
        i = g + c;
        if(i <= N && a[i] <= x) {
            x -= a[i];
            c = i;
        }
    }
    return c + 1;
}

void Add (int x,int data) {
    for(;x<=N;x+=lowbit(x)) a[x] += data;
}

int main() {
    scanf("%d%d",&N,&m); --m;
    for(int i=1;i<=N;++i) a[i] = lowbit(i);    
    for(int n=N;n>=1;--n) {
        (pos += m) %= n;
        p = search (pos);
        printf("%d ",p);
        Add (p,-1);
    }
    return 0;
}

