#include<string>   
#include<stdio.h>
#include<stdlib.h>
#include<math.h>
using namespace std;
#define CRL(a) memset(a,0,sizeof(a))
#define MAX 0xfffffff
typedef unsigned long long LL;
typedef  long long ll; 
const double Pi = acos(-1);
const double e = 2.718281828459;
const int mod =1e9+7; 

ll fac(ll x)		//阶乘 
{
	ll ans=1;
	for(int i=1;i<=x;i++)
		ans=(ans*i)%mod;
	return ans;
}

ll x,y;
