{
    if(b==0)
    {
        x=1;y=0;
        return a;
    }
    ll r=exgcd(b,a%b);
    ll c=x;
    x=y;
    y=c-a/b*y;
    return r;
}

ll qpow(ll a,ll b)		//快速幂 
{
    ll ans=1;
