	{
		if(a<b) ans=0;              //特判
		else
		{
			exgcd((fac(b)*fac(a-b))%mod,mod);
			x= x<=0? x+=mod:x;						//保证x>0 
			ans=(((fac(a)*x)%mod)*qpow(2,a-b))%mod;
