#include<cstdio>
#include<iostream>
#define inf 0x3f3f3f3f
using namespace std;
int a[105][105];
int main()
{
	int n,k,x;
	cin>>n>>k;
	for(int i=1;i<=n;i++)
	{
		for(int j=1;j<=n;j++)
		{
			a[i][j]=inf;
		}
	}
	for(int i=1;i<=n;i++)
	{
		cin>>x;
		if(a[i][i%n+1]!=inf)
		{
			a[i][i%n+1]=a[i%n+1][i]=max(x,a[i%n+1][i]);
		}
		else
		{
			a[i][i%n+1]=a[i%n+1][i]=x;
		}
	}
	char s,e;
	for(int i=1;i<=k;i++)
	{
		int val;
		cin>>s>>e>>val;

		int x=s-'A'+1;
		int y=e-'A'+1;
		if(a[x][y]!=inf)
		{
			a[x][y]=a[y][x]=max(val,a[x][y]);
		}
		else
		{
			a[x][y]=a[y][x]=val;
		}
	}
	char n1,n2;
	cin>>n1>>n2;
	int ans1=n1-'A'+1;
	int ans2=n2-'A'+1;
	for(int k=1;k<=n;k++)
	{
		for(int i=1;i<=n;i++)
		{
			for(int j=1;j<=n;j++)
			{
				a[i][j]=min(a[i][j],a[i][k]+a[k][j]);
			}
		}
	} 
	cout<<a[ans1][ans2]<<endl;
