#include<bits/stdc++.h>
#define MAXN 2500
#define INF 20000
#define N(x) x-'A'+1
using namespace std;
typedef pair<int,int> pii;
int g[MAXN][MAXN],n,k,begin,end,a[MAXN],vis[MAXN]; //a为源点到目标的距离，vis为访问标记 
priority_queue<pii,vector<pii>,greater<pii> > q; //小根堆优化，记住pair的排序方式是按第一个元素来，所以距源点距离要放在前面 
void read()
{
	for(int i=1;i<=n;i++)
	{
		int x;
		scanf("%d",&x);
		if(i < n)
		    {
		        g[i][i+1]=max(g[i][i+1],x);
		        g[i+1][i]=max(g[i][i+1],x);
		    }
		else
		    {
		        g[i][1]=max(g[i][i+1],x);
		        g[1][i]=max(g[i][i+1],x);
		    }
	}
	for(int i=1;i<=k;i++)
	{
		char a,b;
		int x,a1,b1;
		cin>>a>>b>>x;
		a1=N(a); b1=N(b);
		g[b1][a1]=g[a1][b1]=max(g[a1][b1],x);
	}
	char a,b;
	cin>>a>>b;
	begin=N(a); end=N(b);
}

void dijstra(int s)
{
	a[s]=0;
	q.push(pii(0,s));
	while(!q.empty())
	{
		pii now=q.top(); q.pop();
		int x=now.second,y=now.first;
		if(vis[x]) continue;
		vis[x]=1;
		for(int i=1;i<=n;i++)
		{
			if(g[x][i] == -1 ) continue;
			if(g[x][i] + a[x] < a[i])
			{
				a[i]=a[x]+g[x][i];
				q.push(pii(a[i],i));
			}
		}
	}
}

int main()
{
	scanf("%d%d",&n,&k);
	for(int i=1;i<=n;i++)
	{
		a[i]=INF;
		for(int j=1;j<=n;j++)
		{
			g[i][j]=-1;
		}
	}
	read();
	dijstra(begin);
	if(begin == end)
	{
		printf("%d",g[begin][end] == -1 ? 0:g[end][end]); //特判，end比begin的字少一些诶 
		return 0; 
	}
	printf("%d",a[end]);
}
```

