#include<iostream>
#include<algorithm>
#include<cstdio>
#include<vector>
#include<queue>
using namespace std;
bool isin1[50][50] , isin2[50];
int map[50][50];
int d[50];
vector<int> w[50];
queue<int> q;
int B , E;
void spfa()
{
    d[B] = 0;
    q.push(B);isin2[B] = true;
    while(!q.empty())
    {
        int li = q.front(); q.pop(); isin2[li] = false;
        for(vector<int>::iterator ite = w[li].begin() ; ite != w[li].end() ; ite++)
        {
            if(d[*ite] > d[li] + map[li][*ite])
            {
                d[*ite] = d[li] + map[li][*ite];
                if(!isin2[*ite])
                {
                    q.push(*ite);isin2[*ite] = true;
                }
            }
        }
    }
}
int main()
{
    int n , k;
    cin>>n>>k;
    for(int i = 0 ; i <= n ; i++)
    {
        d[i] = 10000000;
    }
    for(int i = 1 ; i < n ; i++)
    {
        int x ; cin>>x;
        map[i][i+1] = map[i+1][i] = x;
        w[i].push_back(i+1);
        w[i+1].push_back(i);
    }
    int x; cin>>x; map[n][1] = map[1][n] = x;
    w[n].push_back(1);w[1].push_back(n);
    for(int i = 1 ; i <= k ; i++)
    {
        char x ,y; cin>>x>>y;
        int a = x-'A'+1 , b = y-'A'+1;
