            isin1[a][b] = isin1[b][a] = true;
        }
        if(map[a][b] < l)
            map[a][b] = map[b][a] = l;
    }
    char c1 , c2;
    cin>>c1>>c2;
    B = c1-'A'+1;E = c2-'A'+1;
    if(B == E)
    {
        cout<<map[B][E];
        return 0;
    }
    spfa();
    cout<<d[E];
    return 0;
}
