stringstream ni1,ni2;
string s1,s2;
int zhuanshu(string a){
	int c=a.size();
	int n=1;
	int sum=0;
	for (int i=c-1;i>=0;i--){
		sum+=(a[i]-'0')*n;
		n*=10;
	}
	return sum;
}
