	ri1=zhuanshu(s1.substr(6,2));
	yue2=zhuanshu(s2.substr(4,2));
	ri2=zhuanshu(s2.substr(6,2));//记录下开始和结束的年份月份日份
	ni1<<n1;
	ni1>>nian1;
	ni2<<n2;
