【算法1】：枚举从开始日期到结束日期之间的所有日期，判断其是否是回文。

Code:

```
#include<iostream>
using namespace std;
int ans,month[13]={0,31,28,31,30,31,30,31,31,30,31,30,31};
//判断闰年
bool isleap(int y)
{
	return ((y%4==0&&y%100!=0)||y%100==0);
}
//返回y年m月的天数。
int mday(int y,int m)
{
	return m==2?month[m]+isleap(y):month[m];
}
//判断y年m月d天是否是回文日期
bool is_hw(int y,int m,int d)
{
	int t=d%10*1000+d/10*100+m%10*10+m/10;
	return y==t;
}
int main()
{
	int d1,d2,ys,ms,ds,ye,me,de;
	cin>>d1>>d2;
	ys=d1/10000;
	ms=d1/100%100;
	ds=d1%100;
	ye=d2/10000;
	me=d2/100%100;
	de=d2%100;
	for(int y=ys;y<=ye;y++)
	{
		int m=1,maxm=12;
		if(y==ys) m=ms;
		if(y==ye) maxm=me;
		for(;m<=maxm;m++)
		{
			int d=1,maxd=mday(y,m);
			if(y==ys&&m==ms) d=ds;
			if(y==ye&&m==me) maxd=de;
			for(;d<=maxd;d++)
			{
				if(is_hw(y,m,d)) ans++;
			} 
		}
	}
	cout<<ans;
	return 0; 
}
