#include <cstdio>
#include <map>
#include <cmath>

using namespace std;

int main()
{
    map<char,int> xm,ym;
    int n,x,y,d1,d2;
    scanf("%d",&n);
    char c;
    for (int i=0;i<n;i++)
    {
        scanf("\n%c%d%d",&c,&x,&y);
        xm[c]=x;
        ym[c]=y;
    }

    int x1,y1,x2,y2;
    double k1,k2,ax,ay;
    scanf("%d",&n);
    n-=90;
    scanf("\n%c",&c);
    x1=xm[c];y1=ym[c];
    scanf("%d",&d1);
    d1=180-n-d1;
    k1=tan(3.141592653589793/180*d1);

    scanf("\n%c",&c);
    x2=xm[c];y2=ym[c];
    scanf("%d",&d2);
    d2=180-n-d2;
    k2=tan(3.141592653589793/180*d2);
    ax=(y2-y1+k1*x1-k2*x2)/(k1-k2);
    ay=k1*(ax-x1)+y1;
    printf("%.2lf %.2lf\n",ax,ay);
}

```
