			n -= tem - pow[i - 1] + 1;
		} n -= (fulllen = m - pow[len - 1]) + 1;
		if (n < 0) { printf("0\n"); return; }
		if (n == 0) { printf("%lld\n", m); return; }
		lint sub(fulllen * 10), cnt(0);
		if (sub == 0) { printf("0\n"); return; }
		while (n > sub) {
			n -= sub, sub *= 10, ++cnt;
		}
		if (n == sub) printf("%lld\n", pow[cnt + 1] * m - 1);
		else printf("%lld\n", pow[len + cnt] + n - 1);
	}
