}
void Union(int a,int b){
    int x=find(a);
    int y=find(b);
    if(x==y) return;
    if(rank[x]<rank[y]){
        p[x]=y;
    }
    else{
        p[y]=x;
        if(rank[x]==rank[y]) rank[x]++;
    }
    return;    
}
bool same(int a,int b){
