void link(int r,int x,int y){
    int fx=gf(x),fy=gf(y);
    if(fx==fy)return;
    f[fx]=fy;
    rank[fx]=(rank[y]+r+(3-rank[x]))%3;//加上一个3是为了防止出现负数，用%会得到意外的结果
