```cpp
#include<iostream>
#include<cmath>
#include<cstdio>
#include<stdlib.h>
#include<algorithm>
#include<cstring>
#include<vector>
#define MAXN 100005
#define NEXT(x) (eat[dfs(x)])
#define LAST(x) (beeat[dfs(x)]) 
using namespace std;
int m,n,same[MAXN],eat[MAXN],beeat[MAXN],fa,rank[MAXN];
int dfs(int k)
{
	if(k==same[k])
		return k;
	int l=dfs(same[k]);
	same[k]=l; 
	return l;
}
void hb(int x,int y)
{
	int l=dfs(x),h=dfs(y);	
	if(rank[l]>rank[h])
		swap(l,h);
	same[l]=h;
	if(rank[l]==rank[h])
		rank[h]++;
	if(eat[l]!=0)
		eat[h]=eat[l];
	if(beeat[l]!=0)
	beeat[h]=beeat[l];
}
void dabao(int y,int z)
{
	int j=0,k=0,l=0;
	if(eat[dfs(y)]!=0&&eat[dfs(z)]!=0)
	{
		hb(eat[dfs(y)],eat[dfs(z)]);
	}
	if(beeat[dfs(y)]!=0&&beeat[dfs(z)]!=0)
	{
		hb(beeat[dfs(y)],beeat[dfs(z)]);
	}
	hb(y,z);
	j=dfs(y);
	if(eat[dfs(y)]!=0)
		k=dfs(eat[dfs(y)]);
	else if(eat[dfs(z)]!=0)
		k=dfs(eat[dfs(z)]);
	if(beeat[dfs(y)]!=0)
		l=dfs(beeat[dfs(y)]);
	else if(beeat[dfs(z)]!=0)
		l=dfs(beeat[dfs(z)]);
	eat[k]=l;
	beeat[l]=k;
	eat[j]=k;
	beeat[k]=j;
	eat[l]=j;
	beeat[j]=l;	
	eat[0]=0;
	beeat[0]=0;	
}
bool pd(int y,int z)
{
	if( (eat[dfs(y)]==dfs(z)   &&  eat[dfs(y)]!=0)
		||(beeat[dfs(y)]==dfs(z) &&  beeat[dfs(y)]!=0))
	{
		return false;
	}
	return true;
}
void sec(int y,int z)
{
	if(eat[dfs(z)]!=0&&beeat[dfs(y)]!=0)
	{
		dabao(eat[dfs(z)],beeat[dfs(y)]); 
	}
	else if(eat[dfs(z)]!=0)
	{
		eat[dfs(eat[dfs(z)])]=dfs(y);
		beeat[dfs(y)]=eat[dfs(z)];
	}
	else if(beeat[dfs(y)]!=0)
	{
		beeat[dfs(beeat[dfs(y)])]=dfs(z);
		eat[dfs(z)]=beeat[dfs(y)];
	}	
}
int main()
{
	//freopen("chain.in","r",stdin);
	//freopen(".out","w",stdout);
	scanf("%d%d",&n,&m);
	for(int i=0;i<MAXN;i++)
		same[i]=i;
	int x,y,z;
	for(int i=1;i<=m;i++)
	{
		scanf("%d%d%d",&x,&y,&z);
		if((y>n||z>n)||(x==2&&y==z))
		{
			fa++;
			continue;
		}
		if(x==1)
		{
			if(pd(y,z)==false)
			{
				fa++; 
				continue;
			}
			dabao(y,z);
		}
		else
		{
			if(dfs(y)==dfs(z)||beeat[dfs(y)]==dfs(z))
			{
				fa++;
				continue;
			}
			if(eat[dfs(y)]!=0&&beeat[dfs(z)]!=0)
			{
				if(pd(y,beeat[dfs(z)])==true&&pd(eat[dfs(y)],z)==true)
				{
					dabao(y,beeat[dfs(z)]);
					dabao(eat[dfs(y)],z);
					sec(y,z);
				}
				else
					fa++;	
			}
			else if(eat[dfs(y)]!=0&&beeat[dfs(z)]==0)
			{
				if(pd(eat[dfs(y)],z)==true)
				{
					dabao(eat[dfs(y)],z);
					beeat[dfs(z)]=dfs(y);
					sec(y,z);
				}
				else
					fa++;
			}
			else if(eat[dfs(y)]==0&&beeat[dfs(z)]!=0)
			{
				if(pd(y,beeat[dfs(z)])==true)
				{
					dabao(y,beeat[dfs(z)]);
					eat[dfs(y)]=dfs(z);
					sec(y,z);
				}
				else
					fa++;
			}
			else if(eat[dfs(y)]==0&&beeat[dfs(z)]==0)
			{
				eat[dfs(y)]=dfs(z);
				beeat[dfs(z)]=dfs(y);
				sec(y,z);
			}
		}
	}
	printf("%d",fa);
    return 0;
}

