{
	cin>>n>>t;
	for(i=1;i<=n;i++)cin>>a[i];
	for(i=1;i<=n;i++)cin>>b[i];
	for(i=1;i<=n;i++)for(j=i;j>=0;j--)f[j]=max(f[j]-a[i],j?(f[j-1]+a[i]+(j%t?0:b[i])):-210000000);
	for(j=0;j<=n;j++)ans=max(ans,f[j]);
