		for (int j=1;j<=i;j++){
			dp[i][j]=dp[i-1][j-1]+s[i];
			if (j%t==0) dp[i][j]+=b[i];
			dp[i][j]=max(dp[i][j],dp[i-1][j]-s[i]);
		}
