  for i:=1 to n do 
    begin
      readln(a[i]);
      if pos(a[i],s)<>0 then p:=true;
    end;
  if not p then //如果每个单词都没出现，则不能拆分
    begin
      writeln(0);
      halt;
    end;
  f[0]:=0;//边界
  for i:=1 to length(s) do 
    begin
      for j:=1 to i do 
        begin
          k:=ff(copy(s,j,i-j+1));
          if (f[i]<f[j-1]+k)and(k>0) then f[i]:=f[j-1]+k;
        end;
