		else ss=ss+s[i];
		nu++;
		if(nu==3) ss=ss+'-';
	}
	se.insert(ss);
	cnt[ss]++;
}

int main()
{
	scanf("%d",&n);
	for(int i=1;i<=n;++i) work();
	bool f=0;
	for(set<string>::iterator i=se.begin();i!=se.end();++i) if(cnt[*i]>1) cout<<*i<<" "<<cnt[*i]<<endl,f=1;
	if(!f) printf("No duplicates.\n");
