```cpp
#include<iostream>
#include<cstdio>
#include<cstring>
using namespace std;
const int maxn = 129;
int A[maxn][maxn],tmp[maxn][maxn],cnt = 0,d,tmax = -1;
void solve()
{
    for(int i = 0;i<=maxn-1;i++)
    for(int j = 0;j<=maxn-1;j++)
    for(int k = -d;k<=d;k++)
    {
        for(int m = -d;m<=d;m++)
        {
            if(i+k>=0&&i+k<=maxn-1&&j+m>=0&&j+m<=maxn-1)
            tmp[i][j] += A[i+k][j+m];
        }
    }
    for(int i = 0;i<maxn-1;i++)
    for(int j = 0;j<maxn-1;j++)
    if(tmp[i][j]>tmax) tmax = tmp[i][j];
    for(int i = 0;i<=maxn-1;i++)
    for(int j = 0;j<=maxn-1;j++)
    if(tmp[i][j]==tmax) cnt++;
}
int main()
{
    int n,x,y,z;
    cin>>d>>n;
    for(int i = 1;i<=n;i++)
    {
        cin>>x>>y>>z;
        A[x][y] = z;
    }
    solve();
    cout<<cnt<<" "<<tmax<<endl;
    return 0;
}
