#include<iostream>
using namespace std;
bool a[101][101],b[101][101],ans[101];  //友情提示：一定要用bool！！！
int fx[5]={0,-1,0,1,0};
int fy[5]={0,0,1,0,-1};   //方向数组
int main()
{
	for(int i=1;i<=3;i++)
	{
		for(int j=1;j<=3;j++)
		{
			cin>>a[i][j];
			a[i][j]=!a[i][j];
		}
	}       //输入
	int t=1;
	for(int i=1;i<=3;i++)
	{
		for(int j=1;j<=3;j++)
		{
			for(int k=1;k<=4;k++)
			{
				int x=i+fx[k];
				int y=j+fy[k];
				if(x>=1&&x<=3&&y>=1&&y<=3)
				{
					b[t][x*3-3+y]=a[x][y];
				}
			}
			b[t][i*3-3+j]=a[i][j];
			b[t][10]=a[i][j];
			t++;
		}
	}   //将这九盏灯的情况转化为异或方程组
	for(int k=1;k<=8;k++)
	{
		int p=k;
		for(int i=k;i<=9;i++)
		{
			if(b[i][k]>b[p][k])p=i;
		}
		for(int i=1;i<=10;i++)
		{
			swap(b[k][i],b[p][i]);
		}
		for(int i=k+1;i<=9;i++)
		{
			if(!b[i][k])continue;  //要注意分母为0的情况
			for(int j=k;j<=10;j++)
			{
				b[i][j]=b[k][j]^b[i][j];
			}
		}
	}     //解异或方程组
	for(int i=9;i>=1;i--)
	{
		if(!b[i][i])continue;  //要注意分母为0的情况
		int s=0;
		for(int j=i+1;j<=9;j++)
		{
			if(b[i][j])s+=ans[j];
		}
		ans[i]=b[i][10]-s;
	}    //求解
	int sum=0;
	for(int i=1;i<=9;i++)
	{
		if(ans[i])sum++;
	}        //解中有几个为1答案就为几
	cout<<sum<<endl;   //输出
	return 0;
}
