### 高清无码的源代码


```cpp
#include <iostream>
#include <queue>

using namespace std;

bool map[5][5], choice[10];
int step, next_x[]{ 0, -1, 0, 1, 0 }, next_y[]{ -1, 0, 1, 0, 0 };

int encode(void);
void decode(int code);
void touch(int pos);
bool check(void);
void bfs(void);
void PrintMap(void);

int main(void)
{
	for (int i = 1; i <= 3; i++)
	{
		for (int j = 1; j <= 3; j++)
		{
			cin >> map[i][j];
		}
	}
	bfs();
	system("pause");
	return 0;
}

int encode(void)
{
	int code = 0;
	for (int i = 1; i <= 3; i++)
	{
		for (int j = 1; j <= 3; j++)
		{
			code <<= 1;
			if (map[i][j])
			{
				code |= 1;
			}
		}
	}
	for (int i = 1; i <= 9; i++)
	{
		code <<= 1;
		if (choice[i])
		{
			code |= 1;
		}
	}
	int temp = step;
	for (int i = 3; i >= 0; i--)
	{
		code <<= 1;
		code |= temp & 1;
		temp >>= 1;
	}
	return code;
}

void decode(int code)
{
	step = 0;
	for (int i = 0; i < 3; i++)
	{
		if (code & 1)
		{
			step |= 1;
		}
		code >>= 1;
		step <<= 1;
	}
	step |= code & 1;
	code >>= 1;
	for (int i = 9; i >= 1; i--)
	{
		choice[i] = code & 1;
		code >>= 1;
	}
	for (int i = 3; i >= 1; i--)
	{
		for (int j = 3; j >= 1; j--)
		{
			map[i][j] = code & 1;
			code >>= 1;
		}
	}
}

void touch(int pos)
{
	int x = (pos - 1) / 3 + 1;
	int y = (pos - 1) % 3 + 1;
	for (int i = 0, tx, ty; i < 5; i++)
	{
		tx = x + next_x[i], ty = y + next_y[i];
		map[tx][ty] = !map[tx][ty];
	}
}

bool check(void)
{
	for (int i = 1; i <= 3; i++)
	{
		for (int j = 1; j <= 3; j++)
		{
			if (!map[i][j])
			{
				return false;
			}
		}
	}
	return true;
}

void bfs(void)
{
	queue<int> que;
	que.push(encode());
	while (!que.empty())
	{
		static int cur = 0;
		cur = que.front();
		que.pop();
		decode(cur);
		if (check())
		{
			cout << step;
			return;
		}
		step++;
		for (int i = 1; i <= 9; i++)
		{
			if (!choice[i])
			{
				choice[i] = true;
				touch(i);
				que.push(encode());
				touch(i);
				choice[i] = false;
			}
		}
	}
}

void PrintMap(void)
{
	for (int i = 1; i <= 3; i++)
	{
		for (int j = 1; j <= 3; j++)
		{
			cout << map[i][j] << ' ';
		}
		cout << endl;
	}
	cout << endl;
}
```

## 这一题的数据并不大，用暴搜可以水过。

#### 不过要确认搜索的边界条件是什么，因为本题变化只有0变1或者1变0，所以每个位置最多只能点一次。


#### 举个栗子： 点（1,1）（1,2）（1,1）和单点（1,2）是一样的。你们可以手推一下
```cpp
#include<iostream>
#include<cstdio>
using namespace std;
int ans=1<<30;
bool b[4][4],a[5][5];//b保证每个位置只点一次
inline int read(){//快读
	int x=0,f=1;char c=getchar();
	while(c<'0'||c>'9'){f=-1;c=getchar();}
	while(c>='0'&&c<='9'){x=x*10+c-'0';c=getchar();}
	return x*f;
}
inline int change(int x,int y){//a的数组大小开到[5][5]，这里就不用if判断
	a[x][y]^=1;//按位异或
	a[x-1][y]^=1;
	a[x][y-1]^=1;
	a[x+1][y]^=1;
	a[x][y+1]^=1;
	return 0;
}
inline bool check(){//检查是否全部点亮
	int t=0;
	for(int i=1;i<=3;i++)for(int j=1;j<=3;j++)if(a[i][j])t++;
	if(t==9)return 1;
	return 0;
}
int f(int sum){
	for(int i=1;i<=3;i++)for(int j=1;j<=3;j++)if(!b[i][j]){
		change(i,j);b[i][j]=1;
		if(check())ans=min(ans,sum+1);//最小次数
		f(sum+1);
		change(i,j);b[i][j]=0;//回溯
	}
	return 0;
}
int main(){
	for(int i=1;i<=3;i++)for(int j=1;j<=3;j++)a[i][j]=read();
	if(check()){cout<<0<<endl;return 0;}//如果全都点亮，就输出0
	for(int i=1;i<=3;i++)for(int j=1;j<=3;j++){
		change(i,j);b[i][j]=1;
		if(check()){cout<<1<<endl;return 0;}
		f(1);
		change(i,j);b[i][j]=0;
	}
	cout<<ans<<endl;
	return 0;
} 
