    	for(int j=2;i*j<=10000;j++)
    	  pd[i*j]=0;
    for(int i=2;i<=n;i++)
    {
        int b=i;
        for(int j=2;j<=n;j++)
          if(pd[j])
            while(b%j==0)
            {
                a[j]++;
