#include<cstdio>
#define LL long long
using namespace std;
struct arr{LL a, c;};
const arr zero = (arr){1, 0};
LL m, a, c, x, n, g;
LL mm(LL x, LL y){
    if (!y) return 0;
    LL ans = mm(x, y >> 1) << 1;
    if (y & 1) ans += x;
    while (ans >= m) ans -= m;
    return ans;
}
inline arr mul(arr a, arr b){return (arr){mm(b.a, a.a),(mm(b.a, a.c) + b.c)%m};}
arr self_GO(arr a, LL t){
    if (!t) return zero;
    arr p = self_GO(a, t >> 1);
    p = mul(p, p);
    if (t&1) return mul(a, p);
    else return p;
}
int main(){
    scanf("%lld%lld%lld%lld%lld%lld", &m, &a, &c, &x, &n, &g);
    arr s = (arr){a, 1}, p = self_GO(s, n);
    printf("%lld", (mm(p.a, x) + mm(p.c, c))%m%g);
    return 0;
}

