using namespace std;
const int maxn = 101;
#define ld long double

int n, m;
struct chain{int v, w; chain* next;} *head[maxn], *tail[maxn];
ld I[maxn];

void advanced_dijkstra(int);

int main()
{
	int i, u, v, w;
	scanf ("%d%d", &n, &m);
	for (i = 1; i <= n; ++i)
	{
		head[i] = new chain;
		tail[i] = head[i];
	}
	for (i = 0; i < m; ++i)
