    n=read();m=read();k=read();
    for(int i=1;i<=n;i++)
    for(int j=1;j<=m;j++)
    a[i][j]=read();//读入
    f[1][0][1]=f[0][1][1]=1;//初始化，当初我傻傻地将第0行与第0列全部赋为1，然后爆炸了。。。
    for(int i=1;i<=n;i++)
    for(int j=1;j<=m;j++)//枚举每个位置
    for(int l=0;l<k;l++){//枚举每个能达到的数
        if(f[i-1][j][l])
        f[i][j][(l*a[i][j])%k]=1;//转移
        if(f[i][j-1][l])
        f[i][j][(l*a[i][j])%k]=1;//转移
    }
    for(int i=0;i<k;i++)if(f[n][m][i])ans++;//计算个数
    printf("%d\n",ans);
