    for (i=1;i<=n;i++)
        for (j=1;j<=m;j++)
        {
            scanf("%d",&a[i][j]);
            a[i][j]%=p;//一开始就%p，反正最后也是要%p的
        }            
    memset(f,0,sizeof(f));
    f[1][1][a[1][1]]=true;//初始化
    linshi=a[1][1];
    for (i=2;i<=n;i++)
    {
        linshi=(linshi*a[i][1])%p;
        f[i][1][linshi]=true;//初始化
    }
    linshi=a[1][1];
    for (i=2;i<=m;i++)
    {
        linshi=(linshi*a[1][i])%p;
        f[1][i][linshi]=true;//初始化
    }
    for (i=1;i<=n;i++)    
        for (j=1;j<=m;j++)
            for (k=0;k<=p;k++)
                if (f[i-1][j][k]==true||f[i][j-1][k]==true)//状态转移方程
                    f[i][j][(k*a[i][j])%p]=true;
    for (i=0;i<=p;i++)
        if (f[n][m][i])
            s++;            //统计
