}
const int mod=9999973;
int n,m;
int dp[110][110][110];

long long C(int m,int k) {
	if(m<k) return 0;
	long long ans=1;
	for(int i=m;i>m-k;i--)
		ans=ans*i;
	long long fm=1;
	for(int i=k;i>=2;i--) 
		fm=fm*i;
	ans/=fm;
	return ans%mod;
}

int main() {
	n=read();
	m=read();
	dp[1][0][0]=1;
	dp[1][1][0]=m;
