	for(int i=2;i<=n;i++) {
		for(int x=0;x<=m;x++) {
			for(int y=m-x;y>=0;y--) {
				dp[i][x][y]=dp[i-1][x][y];
				if(x>0) 
					dp[i][x][y]=(dp[i][x][y]+dp[i-1][x-1][y]%mod*(m-y-x+1)%mod)%mod;
				if(y>0) {
