				if(y>0)

				if(y>1) 
					dp[i][x][y]=(dp[i][x][y]+dp[i-1][x+2][y-2]%mod*C(x+2,2)%mod)%mod;
			}
		}
	}
	long long ans=0;
	for(int x=0;x<=m;x++) 
		for(int y=m-x;y>=0;y--) 
			ans=(ans+dp[n][x][y])%mod;
	printf("%lld",ans);
	return 0;
}
