            scanf("%d",&nd->g);
            a[nd->g]++; //存入同样国籍出现次数
            if (a[nd->g]==1) sum++; //如果此类国籍等于1,那么总国籍数目++
            q.push(*nd); //将临时结构体压入队列中
        }
        b=q.back(); //求队列尾
        f=q.front(); //求队列头
        while (b.t-f.t>=86400) //判断队列尾减去队列头是否有86400分钟
        {
            a[f.g]--; //减去同样国籍出现次数
            if (a[f.g]==0) sum--; //如果此类国籍等于0,那么总国籍数目--
            q.pop(); //弹出队列头
            f=q.front(); //更新队列头
        }
        printf("%d\n",sum);
    }
    return 0;
}
