int main(){
//	freopen("port.in","r",stdin);
//	freopen("port.out","w",stdout);
	scanf("%d",&n);
	for(int i=1;i<=n;i++){
		scanf("%d%d",&t,&m);
		while(p.empty()==false){//如果队列不为空 
			h=p.front();//提取 
			if(h.t<=t-86400){//如果大于了时间 
				ton[h.s]--;//这一国籍的人减一 
				if(ton[h.s]==0) ans--;//如果国籍中没有一个人就失去了答案 
				p.pop();//弹出 
				continue;//继续 
			}
			break;//否则弹出(因为是严格递增的) 
		}
		for(int i=1;i<=m;i++){
			scanf("%d",&l);
			h.s=l;
			h.t=t;
			ton[l]++;
			if(ton[l]==1) ans++;//如果这是第一个人答案就加一 
			p.push(h);//压进去 
		}
		cout<<ans<<endl;//输出 
	}
	return 0;
}
