const int day=86400;
using namespace std;
struct people
{
	int t,x;
}a[30001];
int n,t,k,cnt;
queue <int> num;//蜜汁STL
inline int read()//快读
{
	int f=1,x=0;
	char c=getchar();
	while(c<'0'||c>'9')
	{
		if(c=='-')
			f=-1;
		c=getchar();
	}
	while(c>='0'&&c<='9')
	{
