    for(i=1;i<=n;i++){
        while(t[end]<=t[i]-86400){
            for(j=0;j<k[end];j++){
                cnt[x[end][j]]--;
                if(!cnt[x[end][j]]){
                    tot--;
                }
            }
            end++;
        }
        u=i;
        while(t[u]==t[i]&&!visited[u]){
            visited[u]=true;
            for(j=0;j<k[u];j++){
                if(!cnt[x[u][j]]){
