        while(t - peo[head].t >= 86400 && head < tail)//刚才提到过的，如果这艘船停满24小时并且这艘船上有人 
        { 
        	tot[peo[head].k]--;//将属于这个国籍的人递减 
        	if(tot[peo[head].k] == 0) ans--;//如果这个国籍的人全走了，ans-- 
            head++;//将他们出队 
        }
        printf("%d\n",ans);
    }
    return 0;
}
